import React, { useState, useEffect } from 'react';
import Map from '../components/Map'; // Ajustez le chemin relatif selon le besoin
import { ResponsiveContainer, BarChart, Bar, CartesianGrid, Tooltip, Legend, YAxis, XAxis } from 'recharts';
import axios from 'axios';

const Home = () => {
    const [data, setData] = useState([]);
    const center = [46.2276, 2.2137]; // Coordonnées pour centrer la carte sur la France
    const zoom = 6; // Niveau de zoom ajusté pour voir toute la France

    useEffect(() => {
        // Faire une requête à l'API pour obtenir les données
        const fetchData = async () => {
            try {
                const response = await axios.get('http://localhost:4000/api/age-distribution');
                const ageDistribution = response.data;

                // Transformer les données pour le graphique
                const chartData = [
                    { name: "Femmes 65-79", value: ageDistribution.femmes_65_79 },
                    { name: "Femmes 80+", value: ageDistribution.femmes_80_plus },
                    { name: "Hommes 65-79", value: ageDistribution.hommes_65_79 },
                    { name: "Hommes 80+", value: ageDistribution.hommes_80_plus }
                ];

                setData(chartData);
            } catch (error) {
                console.error('Error fetching data:', error);
            }
        };

        fetchData();
    }, []);

    return (
        <div className="home">
            <div className="home-content">
                <Map center={center} zoom={zoom} />
                <div className="chart-container">
                    <ResponsiveContainer width="100%" height={400}>
                        <BarChart data={data}>
                            <CartesianGrid strokeDasharray="3 3" />
                            <XAxis dataKey="name" />
                            <YAxis />
                            <Tooltip />
                            <Legend />
                            <Bar dataKey="value" fill="#8884d8" />
                        </BarChart>
                    </ResponsiveContainer>
                </div>
            </div>
            {/* Autres composants si nécessaire */}
        </div>
    );
};

export default Home;
