import React, { useState, useEffect } from 'react';
import Map from '../components/Map'; // Ajustez le chemin relatif selon le besoin
import { ResponsiveContainer, BarChart, Bar, CartesianGrid, Tooltip, Legend, YAxis, XAxis } from 'recharts';
import axios from 'axios';

const Home = () => {
    const [data, setData] = useState([]);
    const [selectedRegion, setSelectedRegion] = useState(null);
    const center = [46.2276, 2.2137]; // Coordonnées pour centrer la carte sur la France
    const zoom = 6; // Niveau de zoom ajusté pour voir toute la France

    const fetchData = async (region = null) => {
        try {
            const url = region 
                ? `http://localhost:4000/api/age-distribution/${region}` 
                : 'http://localhost:4000/api/age-distribution';
            const response = await axios.get(url);
            const ageDistribution = response.data;

            console.log("Fetched data:", ageDistribution); // Log pour vérifier les données

            // Transformer les données pour le graphique
            const chartData = [
                { name: "F(65-79)", total: ageDistribution.femmes_65_79 },
                { name: "F(80+)", total: ageDistribution.femmes_80_plus },
                { name: "H(65-79)", total: ageDistribution.hommes_65_79 },
                { name: "H(80+)", total: ageDistribution.hommes_80_plus }
            ];

            console.log("Chart data:", chartData); // Log pour vérifier les données du graphique

            setData(chartData);
        } catch (error) {
            console.error('Error fetching data:', error);
            setData([]);
        }
    };

    useEffect(() => {
        fetchData(); // Charger les données de toute la France au montage
    }, []);

    const handleRegionClick = (region) => {
        setSelectedRegion(region);
        fetchData(region);
    };

    return (
        <div className="home">
            <div className="home-content">
                <Map center={center} zoom={zoom} onRegionClick={handleRegionClick} />
                <div className="chart-container">
                    <h2>
                        Répartition homme et femme par tranche d'âge {selectedRegion ? `en ${selectedRegion}` : 'en France'}
                    </h2>
                    <ResponsiveContainer width="100%" height={400}>
                        <BarChart data={data} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
                            <CartesianGrid strokeDasharray="3 3" />
                            <XAxis dataKey="name" />
                            <YAxis tickFormatter={(value) => new Intl.NumberFormat('fr').format(value)} />
                            <Tooltip />
                            <Legend />
                            <Bar dataKey="total" fill="#0000FF" />
                        </BarChart>
                    </ResponsiveContainer>
                </div>
            </div>
            {/* Autres composants si nécessaire */}
        </div>
    );
};

export default Home;
