import React, { useState, useEffect } from 'react';
import Map from '../components/Map'; // Ajustez le chemin relatif selon le besoin
import { ResponsiveContainer, BarChart, Bar, CartesianGrid, Tooltip, Legend, YAxis, XAxis, PieChart, Pie, Cell } from 'recharts';
import axios from 'axios';

const Home = () => {
    const [data, setData] = useState([]);
    const [selectedRegion, setSelectedRegion] = useState(null);
    const center = [46.2276, 2.2137]; // Coordonnées pour centrer la carte sur la France
    const zoom = 6; // Niveau de zoom ajusté pour voir toute la France

    const fetchData = async (region = null) => {
        try {
            const url = region 
                ? `http://localhost:4000/api/age-distribution/${region}` 
                : 'http://localhost:4000/api/age-distribution';
            const response = await axios.get(url);
            const ageDistribution = response.data;

            console.log("Fetched data:", ageDistribution); // Log pour vérifier les données

            // Transformer les données pour le graphique
            const chartData = [
                { name: "F(65-79)", value: ageDistribution.femmes_65_79 },
                { name: "F(80+)", value: ageDistribution.femmes_80_plus },
                { name: "H(65-79)", value: ageDistribution.hommes_65_79 },
                { name: "H(80+)", value: ageDistribution.hommes_80_plus }
            ];

            console.log("Chart data:", chartData); // Log pour vérifier les données du graphique

            setData(chartData);
        } catch (error) {
            console.error('Error fetching data:', error);
            setData([]);
        }
    };

    useEffect(() => {
        fetchData(); // Charger les données de toute la France au montage
    }, []);

    const handleRegionClick = (region) => {
        setSelectedRegion(region);
        fetchData(region);
    };

    // Calcule les pourcentages pour le PieChart
    const totalPopulation = data.reduce((acc, cur) => acc + cur.value, 0);
    const pieData = data.map(item => ({
        ...item,
        percent: ((item.value / totalPopulation) * 100).toFixed(2) // Calcul du pourcentage
    }));

    const COLORS = ['#0088FE', '#00C49F', '#FFBB28', '#FF8042'];

    return (
        <div className="home">
            <div className="home-content">
                <Map center={center} zoom={zoom} onRegionClick={handleRegionClick} />
                <div className="chart-container">
                    <h2>
                        Nombre homme et femme par tranche d'âge {selectedRegion ? `en ${selectedRegion}` : 'en France'}
                    </h2>
                    <ResponsiveContainer width="100%" height={400}>
                        <BarChart data={data}>
                            <CartesianGrid strokeDasharray="3 3" />
                            <XAxis dataKey="name" />
                            <YAxis />
                            <Tooltip />
                            <Legend />
                            <Bar dataKey="value" fill="#0000FF" />
                        </BarChart>
                    </ResponsiveContainer>
                    <ResponsiveContainer width="100%" height={400}>
                        <PieChart>
                            <Pie
                                data={pieData}
                                dataKey="percent"
                                nameKey="name"
                                cx="50%"
                                cy="50%"
                                outerRadius={150}
                                fill="#8884d8"
                                label
                            >
                                {pieData.map((entry, index) => (
                                    <Cell key={`cell-${index}`} fill={COLORS[index % COLORS.length]} />
                                ))}
                            </Pie>
                            <Tooltip />
                            <Legend />
                        </PieChart>
                    </ResponsiveContainer>
                </div>
            </div>
            {/* Autres composants si nécessaire */}
        </div>
    );
};

export default Home;
